namespace Plugin\TwoFactorAuthCustomer42\Repository;

use Eccube\Repository\AbstractRepository;
use Doctrine\Persistence\ManagerRegistry;
use Plugin\TwoFactorAuthCustomer42\Entity\TwoFactorAuthConfig;

/**
 * TwoFactorAuthConfigRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TwoFactorAuthConfigRepository extends AbstractRepository
{
    /**
     * TwoFactorAuthConfigRepository constructor.
     *
     * @param ManagerRegistry $registry
     */
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, TwoFactorAuthConfig::class);
    }

    /**
     *
     * @return $result
     */
    public function findOne()
    {
        $qb = $this->createQueryBuilder('c')->setMaxResults(1)->select('c');

        // å®Ÿè¡Œ
        $result = null;
        $results = $qb->getQuery()->getResult();
        if (!is_null($results) && count($results) > 0) {
            $result = $results[0];
        }

        return $result;
    }

}
